import 'package:flutter/material.dart';
import 'package:audioplayers/audioplayers.dart';

import 'other.dart';

class MusicApp extends StatefulWidget {
  const MusicApp({Key key}) : super(key: key);
  

  @override
  State<MusicApp> createState() => _MusicAppState();
}

class _MusicAppState extends State<MusicApp> {
  List musicList = [
    { 
      'title': "Let SomeBody Go",
      'singer': "Coldplay ft. Selena Gomez",
      'url':
          "https://msicoursematerial.s3.us-west-2.amazonaws.com/Coldplay+X+Selena+Gomez+-+Let+Somebody+Go+(Official+Video).mp3",
      'coverUrl':
          "data:image/jpeg;base64,"
    },
    {
      "title": "La min Lay",
      "singer": "R Zar Ni",
      "url":
          "https://msicoursematerial.s3.us-west-2.amazonaws.com/-La_min_Lay_(getmp3.pro).mp3",
      "coverUrl":
          "data:image/jpeg;base64,"
    },
    {
      "title": "Please Please Please",
      "singer": "The Smiths",
      "url":
          "https://msicoursematerial.s3.us-west-2.amazonaws.com/The_Smiths_-_Please_Please_Please_(getmp3.pro).mp3",
      "coverUrl":
          "data:image/jpeg;base64,"
    },
    {
      "title": "A Way",
      "singer": "Hugh Grant Haley Bennet",
      "url":
          "https://msicoursematerial.s3.us-west-2.amazonaws.com/Hugh_Grant_Haley_Bennett_-_A_Way_(getmp3.pro).mp3",
      "coverUrl":
          "data:image/jpeg;base64,"
    },
    {
      "title": "Min A Chit Kyaunt",
      "singer": "Y Wine",
      "url":"https://msicoursematerial.s3.us-west-2.amazonaws.com/min_a_chit_kyaunt.mp3",
      "coverUrl":
          "data:image/jpeg;base64,"
    },
  ];
  String currentTitle = "";
  String currentCover = "";
  String currentSinger = "";
  IconData btnIcon = Icons.play_arrow;

  AudioPlayer audioPlayer = new AudioPlayer(mode: PlayerMode.MEDIA_PLAYER);
  bool isPlaying = true;
  String currentSong = "";
  void playMusic(String url) async {
    if (isPlaying && currentSong != url) {
      audioPlayer.pause();
      int result = await audioPlayer.play(url);
      if (result == 1) {
        setState(() {
          currentSong = url;
        });
      }
      // } else if (!isPlaying) {
      //   int result = await audioPlayer.play(url);
      //   if (result == 1) {
      //     setState(() {
      //       isPlaying = true;
      //     });
      //   }
    }
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.black12,
      appBar: AppBar(
        backgroundColor: Colors.white,
        title: Text(
          "My Music",
          style: TextStyle(color: Colors.black),
        ),
      ),
      body: Column(
        children: [
          Expanded(
            child: ListView.builder(
                itemCount: musicList.length,
                itemBuilder: (context, index) => other(
                      onTap: () {
                        playMusic(musicList[index]["url"]);
                        setState(() {
                          currentTitle = musicList[index]["title"];
                          currentCover = musicList[index]["coverUrl"];
                          currentSinger = musicList[index]["singer"];
                        });
                        print(index);
                        setState(() {});
                      },
                      title: musicList[index]["title"],
                      singer: musicList[index]["singer"],
                      cover: musicList[index]["coverUrl"],
                    )),
          ),
          Container(
            decoration: BoxDecoration(color: Colors.white, boxShadow: [
              BoxShadow(
                color: Colors.black,
                blurRadius: 8.0,
              ),
            ]),
            child: Column(
              children: [
                Padding(
                  padding:
                      const EdgeInsets.only(bottom: 8.0, left: 8.0, right: 8.0),
                  child: Row(
                    mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                    children: [
                      Container(
                        height: 60,
                        width: 60,
                        decoration: BoxDecoration(
                            image: DecorationImage(
                                image: NetworkImage(currentCover))),
                      ),
                      Expanded(
                        child: Column(
                          mainAxisAlignment: MainAxisAlignment.start,
                          children: [
                            Text(
                              currentTitle,
                              style: TextStyle(
                                fontSize: 18.0,
                                fontWeight: FontWeight.w600,
                              ),
                            ),
                            SizedBox(
                              height: 5.0,
                            ),
                            Text(
                              currentSinger,
                              style: TextStyle(
                                  color: Colors.green, fontSize: 16.0),
                            )
                          ],
                        ),
                      ),
                      IconButton(
                        onPressed: () {
                          if (isPlaying) {
                            audioPlayer.pause();
                            setState(() {
                              btnIcon = Icons.pause;
                              isPlaying = false;
                            });
                          } else {
                            audioPlayer.resume();
                            setState(() {
                              btnIcon = Icons.play_arrow;
                              isPlaying = true;
                            });
                          }
                        },
                        iconSize: 50,
                        icon: Icon(btnIcon),
                      ),
                    ],
                  ),
                )
              ],
            ),
          ),
        ],
      ),
    );
  }
}
